mapper.select:
SELECT f.fid AS fid, fp.material AS patternName, f.img AS img, e.assess AS evaluate, DATE_FORMAT(f.create_time, "%Y-%m-%d") AS createTime, f.status as status FROM fermentation f LEFT JOIN fer_pattern fp ON f.fp_id = fp.fp_id LEFT JOIN evaluate e ON f.eid = e.eid WHERE 1 = 1
AND YEAR( f.create_time ) = #{year}
AND MONTH ( f.create_time ) = #{month}
